/**
 * ACS API Examples
 * 
 * This file contains example queries and responses for the American Community Survey API
 * to be used as reference for implementation and testing.
 */

// API Key configuration
const ACS_API_KEY = process.env.ACS_API_KEY || 'YOUR_CENSUS_API_KEY';

// Base URLs for Census APIs
const GEOCODING_BASE_URL = 'https://geocoding.geo.census.gov/geocoder';
const ACS_BASE_URL = 'https://api.census.gov/data';

// Types for requests and responses
export interface GeocodingRequest {
  x: number;     // longitude
  y: number;     // latitude
  benchmark: string;
  vintage?: string;
  format: string;
}

export interface ACSDataRequest {
  year: string;
  variables: string[];
  for: string;
  in?: string;
}

export interface ACSProxyRequest {
  coordinates: {
    lat: number;
    lng: number;
  };
  variables?: string[];
  year?: string;
  geographyLevel?: 'blockGroup' | 'tract' | 'county' | 'state';
}

// Example queries and expected responses

/**
 * Example 1: Reverse Geocoding - Get Census Geography from Coordinates
 */
export const reverseGeocodingExample = {
  request: {
    x: -73.989,
    y: 40.734,
    benchmark: 'Public_AR_Current',
    vintage: 'Current_Current',
    format: 'json'
  } as GeocodingRequest,

  url: `${GEOCODING_BASE_URL}/geographies/coordinates?x=-73.989&y=40.734&benchmark=Public_AR_Current&vintage=Current_Current&format=json`,

  response: {
    "result": {
      "input": {
        "location": {
          "x": -73.989,
          "y": 40.734
        }
      },
      "addressMatches": [],
      "geographies": {
        "Census Blocks": [
          {
            "GEOID": "360610078001001",
            "CENTLAT": "+40.7344009",
            "CENTLON": "-073.9891358",
            "STATE": "36",
            "COUNTY": "061",
            "TRACT": "007800",
            "BLOCK": "1001"
          }
        ],
        "2020 Census Blocks": [
          {
            "GEOID": "360610078001006",
            "CENTLAT": "+40.7344288",
            "CENTLON": "-073.9889411",
            "STATE": "36",
            "COUNTY": "061",
            "TRACT": "007800",
            "BLOCK": "1006"
          }
        ],
        "Census Block Groups": [
          {
            "GEOID": "360610078001",
            "CENTLAT": "+40.7338933",
            "CENTLON": "-073.9894574",
            "STATE": "36",
            "COUNTY": "061",
            "TRACT": "007800",
            "BLKGRP": "1"
          }
        ]
      }
    }
  }
};

/**
 * Example 2: Get Median Household Income for a Specific Block Group
 */
export const medianIncomeByBlockGroupExample = {
  request: {
    year: "2019/acs/acs5",
    variables: ["B19013_001E", "NAME"],
    for: "block group:1",
    in: "state:36 county:061 tract:007800"
  } as ACSDataRequest,

  url: `${ACS_BASE_URL}/2019/acs/acs5?get=B19013_001E,NAME&for=block%20group:1&in=state:36%20county:061%20tract:007800&key=${ACS_API_KEY}`,

  response: [
    ["B19013_001E", "NAME", "state", "county", "tract", "block group"],
    ["124875", "Block Group 1, Census Tract 78, New York County, New York", "36", "061", "007800", "1"]
  ]
};

/**
 * Example 3: Get Population Demographics for all Tracts in a County
 */
export const countyDemographicsExample = {
  request: {
    year: "2019/acs/acs5",
    variables: ["B01003_001E", "B02001_002E", "B03003_003E", "NAME"],
    for: "tract:*",
    in: "state:36 county:061"
  } as ACSDataRequest,

  url: `${ACS_BASE_URL}/2019/acs/acs5?get=B01003_001E,B02001_002E,B03003_003E,NAME&for=tract:*&in=state:36%20county:061&key=${ACS_API_KEY}`,

  response: [
    ["B01003_001E", "B02001_002E", "B03003_003E", "NAME", "state", "county", "tract"],
    ["5452", "3846", "729", "Census Tract 78, New York County, New York", "36", "061", "007800"],
    ["4522", "2879", "803", "Census Tract 80, New York County, New York", "36", "061", "008000"],
    ["3891", "2105", "1256", "Census Tract 84, New York County, New York", "36", "061", "008400"]
    // Additional results truncated for brevity
  ]
};

/**
 * Example 4: Get Housing Data for a State
 */
export const stateHousingDataExample = {
  request: {
    year: "2019/acs/acs5",
    variables: ["B25077_001E", "B25003_002E", "B25003_003E", "NAME"],
    for: "state:36"
  } as ACSDataRequest,

  url: `${ACS_BASE_URL}/2019/acs/acs5?get=B25077_001E,B25003_002E,B25003_003E,NAME&for=state:36&key=${ACS_API_KEY}`,

  response: [
    ["B25077_001E", "B25003_002E", "B25003_003E", "NAME", "state"],
    ["313700", "3936046", "4012384", "New York", "36"]
  ]
};

/**
 * Example 5: Complete Proxy Request - Coordinates to Demographics
 * This represents a complete flow for the proxy endpoint
 */
export const completeProxyExample = {
  request: {
    coordinates: {
      lat: 40.734,
      lng: -73.989
    },
    variables: ["B19013_001E", "B01003_001E", "B25077_001E"],
    year: "2019",
    geographyLevel: "blockGroup"
  } as ACSProxyRequest,

  response: {
    geography: {
      geoid: "360610078001",
      state: "36",
      county: "061",
      tract: "007800",
      blockGroup: "1",
      name: "Block Group 1, Census Tract 78, New York County, New York"
    },
    data: {
      "B19013_001E": 124875,
      "B01003_001E": 5452,
      "B25077_001E": 1257900
    },
    metadata: {
      "B19013_001E": "Median household income (dollars)",
      "B01003_001E": "Total population",
      "B25077_001E": "Median value (dollars)"
    }
  }
};

/**
 * Example 6: Error Cases
 */
export const errorExamples = {
  invalidApiKey: {
    status: 403,
    error: {
      message: "Invalid API key",
      details: "The API key provided in the request is invalid or inactive"
    }
  },

  invalidGeography: {
    status: 400,
    error: {
      message: "Invalid geography specification",
      details: "The specified geography components do not exist or are improperly formatted"
    }
  },

  invalidCoordinates: {
    status: 400,
    error: {
      message: "Invalid coordinates",
      details: "The specified coordinates are outside the valid range or improperly formatted"
    }
  },

  missingVariables: {
    status: 400,
    error: {
      message: "Missing variables",
      details: "No variables were specified in the request"
    }
  },

  invalidVariable: {
    status: 400,
    error: {
      message: "Invalid variable",
      details: "One or more requested variables are not valid for the specified dataset"
    }
  },

  externalApiTimeout: {
    status: 504,
    error: {
      message: "External API timeout",
      details: "The request to the Census Bureau API timed out"
    }
  }
};

/**
 * Census Variable Metadata - Common ACS Variables
 */
export const commonAcsVariables = {
  "B01003_001E": {
    description: "Total population",
    concept: "TOTAL POPULATION"
  },
  "B19013_001E": {
    description: "Median household income in the past 12 months (in 2019 inflation-adjusted dollars)",
    concept: "MEDIAN HOUSEHOLD INCOME IN THE PAST 12 MONTHS (IN 2019 INFLATION-ADJUSTED DOLLARS)"
  },
  "B25077_001E": {
    description: "Median value (dollars)",
    concept: "MEDIAN VALUE (DOLLARS)"
  },
  "B02001_002E": {
    description: "White alone",
    concept: "RACE"
  },
  "B03003_003E": {
    description: "Hispanic or Latino",
    concept: "HISPANIC OR LATINO ORIGIN"
  },
  "B15003_022E": {
    description: "Bachelor's degree",
    concept: "EDUCATIONAL ATTAINMENT FOR THE POPULATION 25 YEARS AND OVER"
  },
  "B08013_001E": {
    description: "Aggregate travel time to work (in minutes)",
    concept: "AGGREGATE TRAVEL TIME TO WORK (IN MINUTES) OF WORKERS BY SEX"
  },
  "B08012_001E": {
    description: "Means of transportation to work",
    concept: "MEANS OF TRANSPORTATION TO WORK"
  }
};

/**
 * Valid Geography Hierarchies for ACS Requests
 */
export const validGeographyHierarchies = [
  {
    level: "blockGroup",
    requires: ["state", "county", "tract"],
    example: "for=block%20group:1&in=state:36%20county:061%20tract:007800"
  },
  {
    level: "tract",
    requires: ["state", "county"],
    example: "for=tract:*&in=state:36%20county:061"
  },
  {
    level: "county",
    requires: ["state"],
    example: "for=county:*&in=state:36"
  },
  {
    level: "state",
    requires: [],
    example: "for=state:*"
  }
];
```

This TypeScript file provides a comprehensive set of example queries and responses for the ACS API, covering:

1. **Reverse geocoding** to get census geography from coordinates
2. **Different geographic levels** (block group, tract, county, state)
3. **Various census variables** (income, population, housing)
4. **Complete workflow examples** for your proxy endpoint
5. **Error scenarios** to handle in your implementation
6. **Variable metadata** for documentation and UI display
7. **Geography hierarchy information** to validate requests

You can use this file both as documentation for implementation and as test fixtures for your Playwright tests. The examples cover the main use cases for census data integration in your application.

