import { test, expect } from '@playwright/test';
import path from 'path';
import fs from 'fs/promises';
import { parseCSV } from '../basic-parser';

// Base URL for API requests
const API_URL = 'http://localhost:3001'; // Update port if different

test.describe('CSV Parser Handler API Tests', () => {
  // Test setup - ensure we have test CSV files
  test.beforeAll(async () => {
    // Create a test CSV file in the data directory if needed
    const dataDir = path.join(process.cwd(), 'data');
    const testCsvPath = path.join(dataDir, 'test-csv.csv');

    try {
      await fs.access(dataDir);
    } catch {
      await fs.mkdir(dataDir, { recursive: true });
    }

    // Create a simple test CSV file
    await fs.writeFile(testCsvPath, 'name,value\ntest1,100\ntest2,200');
  });

  test('should return CSV data for a valid filename', async ({ request }) => {
    // Make request to the getcsv endpoint
    const response = await request.get(`${API_URL}/getcsv?filename=test-csv.csv`);
    expect(response.ok()).toBeTruthy();

    const body = await response.json();

    // Verify structure and content
    expect(body.success).toBe(true);
    expect(body.headers).toEqual(['name', 'value']);
    expect(body.data.length).toBe(2);
    expect(body.data[0].name).toBe('test1');
    expect(body.data[0].value).toBe('100');
    expect(body.rowCount).toBe(2);
  });

  test('should return 400 for missing filename', async ({ request }) => {
    const response = await request.get(`${API_URL}/getcsv`);
    expect(response.status()).toBe(400);

    const body = await response.json();
    expect(body.error).toBe('Missing or invalid filename parameter');
  });

  test('should return 404 for non-existent file', async ({ request }) => {
    const response = await request.get(`${API_URL}/getcsv?filename=nonexistent.csv`);
    expect(response.status()).toBe(404);

    const body = await response.json();
    expect(body.error).toBe('File not found');
    expect(body.details.filename).toBe('nonexistent.csv');
  });

  test('should include request parameters in response', async ({ request }) => {
    const response = await request.get(`${API_URL}/getcsv?filename=test-csv.csv&extraParam=test`);
    expect(response.ok()).toBeTruthy();

    const body = await response.json();
    expect(body.requestParams).toHaveProperty('filename', 'test-csv.csv');
    expect(body.requestParams).toHaveProperty('extraParam', 'test');
    expect(body).toHaveProperty('timestamp');
  });

  test('should handle filenames with special characters correctly', async ({ request }) => {
    // Create a file with a special character
    const specialFileName = 'test-file with spaces.csv';
    const specialFilePath = path.join(process.cwd(), 'data', specialFileName);
    await fs.writeFile(specialFilePath, 'header1,header2\nvalue1,value2');

    // Use encodeURIComponent to properly encode the filename in the URL
    const encodedFileName = encodeURIComponent(specialFileName);
    const response = await request.get(`${API_URL}/getcsv?filename=${encodedFileName}`);
    expect(response.ok()).toBeTruthy();

    const body = await response.json();
    expect(body.success).toBe(true);
    expect(body.headers).toEqual(['header1', 'header2']);

    // Clean up
    await fs.unlink(specialFilePath);
  });

  test('should return proper error for corrupted CSV files', async ({ request }) => {
    // Create a corrupted CSV file
    const corruptedFileName = 'corrupted.csv';
    const corruptedFilePath = path.join(process.cwd(), 'data', corruptedFileName);
    await fs.writeFile(corruptedFilePath, 'name,value\ntest1,100\ntest2,value,extra');

    const response = await request.get(`${API_URL}/getcsv?filename=${corruptedFileName}`);

    // The behavior here depends on how your parseCSV function handles corrupted files
    // It might either return a 500 error or successfully parse with some adjustments
    if (response.status() === 500) {
      const body = await response.json();
      expect(body.error).toBe('CSV parsing error');
    } else {
      expect(response.ok()).toBeTruthy();
    }

    // Clean up
    await fs.unlink(corruptedFilePath);
  });

  // Clean up test files after all tests
  test.afterAll(async () => {
    try {
      await fs.unlink(path.join(process.cwd(), 'data', 'test-csv.csv'));
    } catch (err) {
      // Ignore cleanup errors
    }
  });
});
