import fs from 'fs/promises';
import { parse } from 'csv-parse/sync';

/**
 * Parses a CSV file and returns its contents as structured data
 * 
 * @param filePath - Path to the CSV file
 * @param delimiter - Optional delimiter character (defaults to comma)
 * @param columns - Whether to convert records to objects using the header row (default: true)
 * @returns Object containing headers and data from the CSV
 */
export async function parseCSV(
  filePath: string, 
  delimiter: string = ',', 
  columns: boolean = true
) {
  try {
    // Read the file content
    const fileContent = await fs.readFile(filePath, { encoding: 'utf-8' });

    // Parse the CSV content
    const parseOptions = {
      delimiter,
      columns,
      skip_empty_lines: true,
      trim: true
    };

    const records = parse(fileContent, parseOptions);

    // If columns is true, the first row is used as header names
    // If columns is false, we need to extract headers differently
    let headers: string[] = [];
    if (columns) {
      // When using columns:true, headers are already used as property names
      // We can extract them from the first record's keys
      headers = records.length > 0 ? Object.keys(records[0]) : [];
    } else {
      // When columns is false, the first row contains headers
      headers = records.length > 0 ? records[0] : [];
      // Remove header row from data
      records.shift();
    }

    return {
      headers,
      data: records
    };
  } catch (error) {
    // Re-throw with more context
    if (error instanceof Error) {
      throw new Error(`Failed to parse CSV file: ${error.message}`);
    }
    throw new Error('Failed to parse CSV file: Unknown error');
  }
}
```

