/**
 * Function to execute a test query and log the results
 */
export async function testACSQuery(queryUrl: string, description: string) {
  console.log(`Testing: ${description}`);
  console.log(`URL: ${queryUrl}`);

  try {
    const response = await fetch(queryUrl);

    if (!response.ok) {
      console.log(`Error: ${response.status} ${response.statusText}`);
      return;
    }

    const data = await response.json();
    console.log("Results:", data.slice(0, 3), "... (truncated)");
    console.log(`Total rows: ${data.length - 1}`); // -1 for header row
    console.log("-----------------------------------");
  } catch (error) {
    console.error("Error executing query:", error);
  }
}

