export interface ACSParams {
  state: string;
  county?: string;
  variables: string[];
  year?: string;
}

export interface FBIParams {
  state: string;
  county?: string;
  place?: string;
  crime?: string;
  year?: string;
}

export type DataQueryParams = ACSParams | FBIParams;

export interface QueryResult {
  headers: string[];
  rows: any[][];
}

// Fetch ACS data from the backend
export const fetchACSData = async (params: ACSParams): Promise<QueryResult> => {
  const response = await fetch('http://localhost:3001/acs/data', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(params),
  });

  if (!response.ok) {
    throw new Error(`Error fetching ACS data: ${response.statusText}`);
  }

  return await response.json();
};

// Fetch FBI data from the backend
export const fetchFBIData = async (params: FBIParams): Promise<QueryResult> => {
  const response = await fetch('http://localhost:3001/fbi/query', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(params),
  });

  if (!response.ok) {
    throw new Error(`Error fetching FBI data: ${response.statusText}`);
  }

  return await response.json();
};
````

## 2. Create DataTable Component

Create a reusable table component for displaying results:

````typescript

import React from 'react';
import './DataTable.css';
import { QueryResult } from '../../services/api';

interface DataTableProps {
  data: QueryResult | null;
  loading: boolean;
}

const DataTable: React.FC<DataTableProps> = ({ data, loading }) => {
  if (loading) {
    return <div className="data-table-loading">Loading data...</div>;
  }

  if (!data || !data.headers || !data.rows) {
    return <div className="data-table-empty">No data to display</div>;
  }

  return (
    <div className="data-table-container">
      <table className="data-table">
        <thead>
          <tr>
            {data.headers.map((header, index) => (
              <th key={index}>{header}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.rows.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {row.map((cell, cellIndex) => (
                <td key={cellIndex}>{cell}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DataTable;
````

