## 4. Create DataExplorer Component

Create a component for the data query interface:

````typescript

import React, { useState } from 'react';
import './DataExplorer.css';
import DataTable from '../../components/DataTable/DataTable';
import { 
  fetchACSData, 
  fetchFBIData, 
  QueryResult, 
  ACSParams, 
  FBIParams 
} from '../../services/api';
import Button from '../../components/Button/Button';

const DataExplorer: React.FC = () => {
  const [dataSource, setDataSource] = useState<'ACS' | 'FBI'>('ACS');
  const [loading, setLoading] = useState<boolean>(false);
  const [queryResult, setQueryResult] = useState<QueryResult | null>(null);

  // ACS parameters
  const [acsState, setAcsState] = useState<string>('RI');
  const [acsCounty, setAcsCounty] = useState<string>('');
  const [acsVariables, setAcsVariables] = useState<string>('B01001_001E,B19013_001E');
  const [acsYear, setAcsYear] = useState<string>('2021');

  // FBI parameters
  const [fbiState, setFbiState] = useState<string>('RI');
  const [fbiCounty, setFbiCounty] = useState<string>('');
  const [fbiPlace, setFbiPlace] = useState<string>('');
  const [fbiCrime, setFbiCrime] = useState<string>('');
  const [fbiYear, setFbiYear] = useState<string>('2020');

  const handleQuerySubmit = async () => {
    setLoading(true);
    setQueryResult(null);

    try {
      if (dataSource === 'ACS') {
        const params: ACSParams = {
          state: acsState,
          variables: acsVariables.split(','),
          year: acsYear,
        };

        if (acsCounty) params.county = acsCounty;

        const result = await fetchACSData(params);
        setQueryResult(result);
      } else {
        const params: FBIParams = {
          state: fbiState,
          year: fbiYear,
        };

        if (fbiCounty) params.county = fbiCounty;
        if (fbiPlace) params.place = fbiPlace;
        if (fbiCrime) params.crime = fbiCrime;

        const result = await fetchFBIData(params);
        setQueryResult(result);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="data-explorer">
      <div className="data-explorer-form">
        <h3>Data Query Tool</h3>

        <div className="form-row">
          <label>
            Data Source:
            <select 
              value={dataSource} 
              onChange={(e) => setDataSource(e.target.value as 'ACS' | 'FBI')}
            >
              <option value="ACS">Census ACS</option>
              <option value="FBI">FBI Crime</option>
            </select>
          </label>
        </div>

        {dataSource === 'ACS' ? (
          <>
            <div className="form-row">
              <label>
                State:
                <input 
                  type="text" 
                  value={acsState} 
                  onChange={(e) => setAcsState(e.target.value)} 
                  placeholder="State (e.g. RI)"
                />
              </label>
              <label>
                County (optional):
                <input 
                  type="text" 
                  value={acsCounty} 
                  onChange={(e) => setAcsCounty(e.target.value)} 
                  placeholder="County name"
                />
              </label>
            </div>
            <div className="form-row">
              <label>
                Variables (comma separated):
                <input 
                  type="text" 
                  value={acsVariables} 
                  onChange={(e) => setAcsVariables(e.target.value)} 
                  placeholder="e.g. B01001_001E,B19013_001E"
                />
              </label>
            </div>
            <div className="form-row">
              <label>
                Year:
                <input 
                  type="text" 
                  value={acsYear} 
                  onChange={(e) => setAcsYear(e.target.value)} 
                  placeholder="Year (e.g. 2021)"
                />
              </label>
            </div>
          </>
        ) : (
          <>
            <div className="form-row">
              <label>
                State:
                <input 
                  type="text" 
                  value={fbiState} 
                  onChange={(e) => setFbiState(e.target.value)} 
                  placeholder="State (e.g. RI)"
                />
              </label>
              <label>
                Year:
                <input 
                  type="text" 
                  value={fbiYear} 
                  onChange={(e) => setFbiYear(e.target.value)} 
                  placeholder="Year (e.g. 2020)"
                />
              </label>
            </div>
            <div className="form-row">
              <label>
                County (optional):
                <input 
                  type="text" 
                  value={fbiCounty} 
                  onChange={(e) => setFbiCounty(e.target.value)} 
                  placeholder="County name"
                />
              </label>
              <label>
                Place (optional):
                <input 
                  type="text" 
                  value={fbiPlace} 
                  onChange={(e) => setFbiPlace(e.target.value)} 
                  placeholder="Place name"
                />
              </label>
            </div>
            <div className="form-row">
              <label>
                Crime Type (optional):
                <input 
                  type="text" 
                  value={fbiCrime} 
                  onChange={(e) => setFbiCrime(e.target.value)} 
                  placeholder="e.g. robbery"
                />
              </label>
            </div>
          </>
        )}

        <div className="form-row">
          <Button onClick={handleQuerySubmit} disabled={loading}>
            {loading ? 'Loading...' : 'Query Data'}
          </Button>
        </div>
      </div>

      <div className="data-table-wrapper">
        <h3>Query Results</h3>
        <DataTable data={queryResult} loading={loading} />
      </div>
    </div>
  );
};

export default DataExplorer;
````

