export interface ACSParams {
  lat: string;
  lon: string;
  variables: string;
  topLevel: string;
  bottomLevel: string;
}

export interface FBIParams {
  granularity: 'national' | 'state' | 'agency';
  lat?: string;
  lon?: string;
  year?: string;
}

export type DataQueryParams = ACSParams | FBIParams;

export interface QueryResult {
  headers: string[];
  rows: any[][];
}

// Fetch ACS data from the backend
export const fetchACSData = async (params: ACSParams): Promise<QueryResult> => {
  const queryParams = new URLSearchParams({
    lat: params.lat,
    lon: params.lon,
    variables: params.variables,
    topLevel: params.topLevel,
    bottomLevel: params.bottomLevel
  });

  const response = await fetch(`http://localhost:3001/acs-proxy?${queryParams.toString()}`);

  if (!response.ok) {
    throw new Error(`Error fetching ACS data: ${response.status} ${response.statusText}`);
  }

  const result = await response.json();

  if (!result.data || !Array.isArray(result.data)) {
    throw new Error('Invalid data format received from server');
  }

  // Format the data for the table
  const headers = result.data[0] || [];
  const rows = result.data.slice(1) || [];

  return { headers, rows };
};

// Fetch FBI data from the backend
export const fetchFBIData = async (params: FBIParams): Promise<QueryResult> => {
  const queryParams = new URLSearchParams({
    granularity: params.granularity,
  });

  if (params.lat) queryParams.append('lat', params.lat);
  if (params.lon) queryParams.append('lon', params.lon);
  if (params.year) queryParams.append('year', params.year);

  const response = await fetch(`http://localhost:3001/fbi-arrest-data?${queryParams.toString()}`);

  if (!response.ok) {
    throw new Error(`Error fetching FBI data: ${response.status} ${response.statusText}`);
  }

  const result = await response.json();

  if (!result.rawData || !result.rawData.data) {
    throw new Error('Invalid data format received from server');
  }

  // Transform the FBI data into a table format
  const data = result.rawData.data;
  const headers = data.length > 0 ? Object.keys(data[0]) : [];
  const rows = data.map(item => headers.map(header => item[header]));

  return { headers, rows };
};
````

