    setQueryResult(null);

    try {
      if (dataSource === 'ACS') {
        const result = await fetchACSData({
          lat,
          lon,
          variables: acsVariables,
          topLevel,
          bottomLevel
        });
        setQueryResult(result);
      } else {
        // For FBI data
        const params = {
          granularity: fbiGranularity,
          year: fbiYear
        } as any;

        // Only add lat/lon for state and agency granularity
        if (fbiGranularity !== 'national') {
          params.lat = lat;
          params.lon = lon;
        }

        const result = await fetchFBIData(params);
        setQueryResult(result);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      setError(`Failed to fetch data: ${error instanceof Error ? error.message : 'Unknown error'}`);
    } finally {
      setLoading(false);
    }
  };

  const granularityOptions = ['all', 'us', 'state', 'county', 'place'];

  return (
    <div className={`data-query-panel ${className || ''}`}>
      <h3>Data Query Tool</h3>

      <div className="data-source-selector">
        <label>
          Data Source:
          <select 
            value={dataSource} 
            onChange={(e) => setDataSource(e.target.value as 'ACS' | 'FBI')}
            className="data-source-select"
          >
            <option value="ACS">Census ACS</option>
            <option value="FBI">FBI Crime</option>
          </select>
        </label>
      </div>

      {dataSource === 'ACS' ? (
        <div className="query-params">
          <div className="param-row">
            <label>
              Latitude:
              <Input 
                value={lat} 
                onChange={(e) => setLat(e.target.value)} 
                placeholder="e.g. 41.8246"
                className="param-input"
              />
            </label>
            <label>
              Longitude:
              <Input 
                value={lon} 
                onChange={(e) => setLon(e.target.value)} 
                placeholder="e.g. -71.4142"
                className="param-input"
              />
            </label>
          </div>

          <div className="param-row">
            <label>
              Variables (comma separated):
              <Input 
                value={acsVariables} 
                onChange={(e) => setAcsVariables(e.target.value)} 
                placeholder="e.g. NAME,S2802_C03_022E"
                className="param-input full-width"
              />
            </label>
          </div>

          <div className="param-row">
            <label>
              Top-level granularity:
              <select 
                value={topLevel}
                onChange={(e) => setTopLevel(e.target.value)}
                className="param-select"
              >
                {granularityOptions.map(option => (
                  <option key={option} value={option}>{option}</option>
                ))}
              </select>
            </label>

            <label>
              Bottom-level granularity:
              <select 
                value={bottomLevel}
                onChange={(e) => setBottomLevel(e.target.value)}
                className="param-select"
              >
                {granularityOptions.map(option => (
                  <option key={option} value={option}>{option}</option>
                ))}
              </select>
            </label>
          </div>
        </div>
      ) : (
        <div className="query-params">
          <div className="param-row">
            <label>
              Granularity:
              <select 
                value={fbiGranularity}
                onChange={(e) => setFbiGranularity(e.target.value as 'national' | 'state' | 'agency')}
                className="param-select"
              >
                <option value="national">National</option>
                <option value="state">State</option>
                <option value="agency">Agency</option>
              </select>
            </label>

            <label>
              Year:
              <Input 
                value={fbiYear} 
                onChange={(e) => setFbiYear(e.target.value)} 
                placeholder="e.g. 2023"
                className="param-input"
              />
            </label>
          </div>

          {fbiGranularity !== 'national' && (
            <div className="param-row">
              <label>
                Latitude:
                <Input 
                  value={lat} 
                  onChange={(e) => setLat(e.target.value)} 
                  placeholder="e.g. 41.8246"
                  className="param-input"
                />
              </label>
              <label>
                Longitude:
                <Input 
                  value={lon} 
                  onChange={(e) => setLon(e.target.value)} 
                  placeholder="e.g. -71.4142"
                  className="param-input"
                />
              </label>
            </div>
          )}
        </div>
      )}

      <div className="query-action">
        <Button onClick={handleQuerySubmit} disabled={loading}>
          {loading ? 'Loading...' : 'Query Data'}
        </Button>
      </div>

      {error && <div className="query-error">{error}</div>}

      {queryResult && (
        <div className="query-results">
          <h4>Results</h4>
          <div className="table-container">
            <table className="data-table">
              <thead>
                <tr>
                  {queryResult.headers.map((header, index) => (
                    <th key={index}>{header}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {queryResult.rows.map((row, rowIndex) => (
                  <tr key={rowIndex}>
                    {row.map((cell, cellIndex) => (
                      <td key={cellIndex}>{cell !== null && cell !== undefined ? String(cell) : 'N/A'}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default DataQueryPanel;
