import { useState } from "react";
import axios from "axios";
import "./DataQueryPanel.css";
import { Location } from "../../components/LocationPicker/LocationPicker";

interface DataQueryPanelProps {
  location: Location | null;
  onQueryResult: (result: QueryResult | null, isLoading: boolean, error: string | null) => void;
}

type DataSource = "ACS" | "FBI";

export interface QueryResult {
  headers: string[];
  rows: any[][];
}

export default function DataQueryPanel({ location, onQueryResult }: DataQueryPanelProps) {
  const [dataSource, setDataSource] = useState<DataSource>("ACS");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // ACS specific state
  const [acsVariables, setAcsVariables] = useState<string>("NAME,S2802_C03_022E");
  const [acsTopLevel, setAcsTopLevel] = useState<string>("state");
  const [acsBottomLevel, setAcsBottomLevel] = useState<string>("place");

  // FBI specific state
  const [fbiGranularity, setFbiGranularity] = useState<string>("national");

  const handleQuerySubmit = async () => {
    if (!location && (dataSource === "FBI" && fbiGranularity !== "national")) {
      const newError = "Location is required for state and agency level FBI data";
      setError(newError);
      onQueryResult(null, false, newError);
      return;
    }

    if (!location && dataSource === "ACS" && acsTopLevel === "state") {
      const newError = "Location is required when top level is set to state";
      setError(newError);
      onQueryResult(null, false, newError);
      return;
    }

    setLoading(true);
    setError(null);
    onQueryResult(null, true, null); // Notify parent that loading started

    try {
      let response;

      if (dataSource === "ACS") {
        // Convert coordinates if available
        const lat = location ? location.lat.toString() : "";
        const lon = location ? location.lng.toString() : "";

        console.log("Sending ACS request with params:", {
          lat, lon, variables: acsVariables, topLevel: acsTopLevel, bottomLevel: acsBottomLevel
        });

        response = await axios.get("http://localhost:3001/acs-proxy", {
          params: {
            lat,
            lon,
            variables: acsVariables,
            topLevel: acsTopLevel,
            bottomLevel: acsBottomLevel
          }
        });

        console.log("ACS response:", response.data);

        // Process ACS data
        if (response.data && response.data.data) {
          const headers = response.data.data[0];
          const rows = response.data.data.slice(1);
          const result = { headers, rows };
          onQueryResult(result, false, null);
        } else {
          const newError = "Invalid response format from ACS API";
          setError(newError);
          onQueryResult(null, false, newError);
        }
      } else {
        // FBI data
        const endpoint = fbiGranularity === "staff" 
          ? "/fbi-staff-data" 
          : "/fbi-arrest-data";

        response = await axios.get(`http://localhost:3001${endpoint}`, {
          params: {
            lat: location?.lat || "",
            lon: location?.lng || "",
            granularity: fbiGranularity === "staff" ? "agency" : fbiGranularity
          }
        });

        // Process FBI data
        if (response.data) {
          let result: QueryResult;

          if (fbiGranularity === "staff") {
            // Process staff data
            const staffData = response.data.staffData;
            const headers = ["Year", "Officer Count", "Civilian Count", "Officers per 1,000", "Population"];
            const rows = Object.keys(staffData.years || {}).map(year => {
              const yearData = staffData.years[year];
              return [year, yearData.officerCount, yearData.civilianCount, yearData.officersPer1000, yearData.population];
            });
            result = { headers, rows };
          } else {
            // Process arrest data
            const arrestData = response.data.data || [];
            const headers = ["Year", "Offense", "State", "Arrest Count"];
            const rows = arrestData.map((item: any) => [
              item.data_year,
              item.offense,
              item.state_abbr,
              item.arrest_count
            ]);
            result = { headers, rows };
          }

          onQueryResult(result, false, null);
        } else {
          const newError = "Invalid response format from FBI API";
          setError(newError);
          onQueryResult(null, false, newError);
        }
      }
    } catch (err: any) {
      console.error("Error fetching data:", err);
      const errorMessage = `Failed to fetch data: ${err.message || "Unknown error"}`;
      setError(errorMessage);
      onQueryResult(null, false, errorMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="data-query-form-container">
      <h2>Data Query</h2>

      <div className="form-group">
        <label>Data Source</label>
        <select 
          value={dataSource} 
          onChange={(e) => setDataSource(e.target.value as DataSource)}
        >
          <option value="ACS">ACS (Census)</option>
          <option value="FBI">FBI Crime Data</option>
        </select>
      </div>

      {dataSource === "ACS" ? (
        <>
          <div className="form-group">
            <label>Variables (comma-separated)</label>
            <input 
              type="text" 
              value={acsVariables} 
              onChange={(e) => setAcsVariables(e.target.value)}
              placeholder="NAME,S2802_C03_022E"
            />
            <small>Example: NAME,S2802_C03_022E (Name and Internet Access)</small>
          </div>

          <div className="form-group">
            <label>Top Level</label>
            <select 
              value={acsTopLevel} 
              onChange={(e) => setAcsTopLevel(e.target.value)}
            >
              <option value="all">All</option>
              <option value="us">US</option>
              <option value="state">State</option>
            </select>
          </div>

          <div className="form-group">
            <label>Bottom Level</label>
            <select 
              value={acsBottomLevel} 
              onChange={(e) => setAcsBottomLevel(e.target.value)}
            >
              <option value="state">State</option>
              <option value="county">County</option>
              <option value="place">Place</option>
            </select>
          </div>
        </>
      ) : (
        <div className="form-group">
          <label>Granularity</label>
          <select 
            value={fbiGranularity} 
            onChange={(e) => setFbiGranularity(e.target.value)}
          >
            <option value="national">National</option>
            <option value="state">State</option>
            <option value="agency">Agency</option>
            <option value="staff">Staff Data</option>
          </select>
        </div>
      )}

      <button 
        className="query-btn" 
        onClick={handleQuerySubmit}
        disabled={loading}
      >
        {loading ? "Loading..." : "Run Query"}
      </button>

      {error && <div className="error-message">{error}</div>}
    </div>
  );
}

import React from 'react';
import './DataQueryPanel.css';
import { QueryResult } from './DataQueryPanel';

interface QueryResultsProps {
  results: QueryResult | null;
  loading: boolean;
  error: string | null;
}

const QueryResults: React.FC<QueryResultsProps> = ({ results, loading, error }) => {
  return (
    <div className="query-results-container">
      <h2>Query Results</h2>

      {loading && <div className="loading-message">Loading data...</div>}

      {error && <div className="error-message">{error}</div>}

      {results ? (
        <div className="results-table-container">
          <table className="results-table">
            <thead>
              <tr>
                {results.headers.map((header, index) => (
                  <th key={index}>{header}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {results.rows.map((row, rowIndex) => (
                <tr key={rowIndex}>
                  {row.map((cell, cellIndex) => (
                    <td key={cellIndex}>{cell}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : !loading && !error ? (
        <p className="no-results-message">No data to display. Run a query to see results.</p>
      ) : null}
    </div>
  );
};

export default QueryResults;

import axios from "axios";
import { useRef, useState } from "react";
import DatePicker from "../components/DatePicker/DatePicker";
import LocationPicker from "../components/LocationPicker/LocationPicker";
import { Location } from "../components/LocationPicker/LocationPicker";
import DataQueryPanel, { QueryResult } from "../features/DataQuery/DataQueryPanel";
import QueryResults from "../features/DataQuery/QueryResults";
import "./Home.css";
import { Planner } from "./Planner";

export default function Home() {
  const [arrivalDate, setArrivalDate] = useState<Date | null>(null);
  const [departureDate, setDepartureDate] = useState<Date | null>(null);
  const [location, setLocation] = useState<Location | null>(null);
  const [showPlanner, setShowPlanner] = useState(false);
  const plannerRef = useRef<HTMLDivElement | null>(null);
  const dataQueryRef = useRef<HTMLDivElement | null>(null);

  // State for query results
  const [queryResults, setQueryResults] = useState<QueryResult | null>(null);
  const [queryLoading, setQueryLoading] = useState<boolean>(false);
  const [queryError, setQueryError] = useState<string | null>(null);

  const handleExploreClick = () => {
    if (location && arrivalDate && departureDate) {
      //call the csv api for activities and also the weather api
      setShowPlanner(true);
      setTimeout(() => {
        plannerRef.current?.scrollIntoView({ behavior: "smooth" });
      }, 100);
    } else {
      alert("Please complete all fields first.");
    }
  };

  const handleCSVLoad = async () => {
    const res = await axios.post("http://localhost:3001/upload-csv");

    if (res.status) {
      console.log("Invalid location data format", res.status);
    }
  };

  // Handler for query results from DataQueryPanel
  const handleQueryResult = (result: QueryResult | null, isLoading: boolean, error: string | null) => {
    setQueryResults(result);
    setQueryLoading(isLoading);
    setQueryError(error);
  };

  return (
    <main className="home-container">
      <section className="hero-section">
        <div className="hero-content">
          <h1>Let's Discover the World Together</h1>
          <p>
            Find the best trips and experiences, tailored to your perfect days.
          </p>

          <div className="search-card">
            <div className="input-wrapper">
              <label>Destination</label>
              <LocationPicker
                value={location}
                onChange={(value) => {
                  setLocation(value);
                  handleCSVLoad();
                }}
              />
            </div>
            <div className="input-wrapper">
              <DatePicker
                label="Arrival"
                selectedDate={arrivalDate}
                onChange={setArrivalDate}
              />
            </div>
            <div className="input-wrapper">
              <DatePicker
                label="Departure"
                selectedDate={departureDate}
                onChange={setDepartureDate}
              />
            </div>
            <button className="explore-btn" onClick={handleExploreClick}>
              Explore
            </button>
          </div>
        </div>

        <div className="canvas-wrapper">
          <div className="overlay" />
          {/* Maybe 3d model goes here if i have time - Roberto*/}
        </div>
      </section>

      <section className="data-query-section">
        <div ref={dataQueryRef} className="data-query-layout">
          <div className="data-query-sidebar">
            <DataQueryPanel location={location} onQueryResult={handleQueryResult} />
          </div>
          <div className="data-query-content">
            <QueryResults results={queryResults} loading={queryLoading} error={queryError} />
          </div>
        </div>
      </section>

      {showPlanner && (
        <section ref={plannerRef}>
          <Planner
            arrivalDate={arrivalDate!}
            departureDate={departureDate!}
            location={location || { name: "", lat: 0, lng: 0 }}
          />
        </section>
      )}
    </main>
  );
}

